import axios from "axios";
import React, { useState, useEffect } from "react";
import { Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./user_detail.css";
import { Modal, Button } from "react-bootstrap";
import Loader from "../../../Components/loader/loader";

function User_detail() {
  const [user, setUser] = useState([]);
  const [showLoader, setshowLoader] = useState(false);
  const errors = [];
  const navigate = useNavigate();
  const user_data = JSON.parse(localStorage.getItem("user_data"));
  const [isShow, setisShow] = React.useState(false);

  useEffect(() => {
    se
    axios
      .get("http://localhost:8000/tracenet_user/", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Token ${user_data.key}`,
        },
      })
      .then((customers) => {
        console.log(customers.data);
        setUser(customers.data);
        localStorage.setItem(
          "tracenet_user_detail",
          JSON.stringify(customers.data)
        );
      })
      .catch((err) => {
        Object.values(err.response.data).map((data) => {
          errors.push(data);
        });
        alert(errors);
      });
  }, []);

  // const initModal = () => {
  //   return setisShow(!false);
  // };

  const handleEdit = () => {
    setisShow(true);
  };

  const handleClickEvent = (data) => {
    let users = user.map((item, key) => {
      let url = "http://localhost:8000/tracenet_user/" + item.id;
      return (

        <tr>
          <td>{key + 1}</td>
          <td>{item.tracenet_username}</td>
          <td>{String(item.is_active)}</td>
          <td>{new Date(item.created_at).toISOString().split("T")[0]}</td>
          <td>{new Date(item.updated_at).toISOString().split("T")[0]}</td>
          <td>
            <div className="btn-group">
              <button
                type="button"
                className="btn btn-default"
                onClick={(e) => {
                  navigate("/tracenet", { state: item });
                }}
              >
                Login
              </button>
              <button
                type="button"
                className="btn btn-default"
                onClick={(e) => {
                  window.confirm("Are you sure you want to delete ?")
                    ? axios
                        .delete(url, {
                          headers: {
                            "Content-Type": "application/json",
                            Authorization: `Token ${user_data.key}`,
                          },
                        })
                        .then((res) => {
                          let filterData = user.filter((item, key) => {
                            console.log(item.id, data.id);
                            return item.id != data.id;
                          });
                          setUser(filterData);
                        })
                        .catch((err) => console.log(err))
                    : console.log("false");
                }}
              >
                Delete
              </button>
              <button
                type="button"
                className="btn btn-default"
                onClick={handleEdit}
              >
                Update
              </button>
              <Modal show={isShow}>
                <Modal.Header closeButton initModal>
                  <Modal.Title>React Modal Popover Example</Modal.Title>
                </Modal.Header>
              </Modal>
            </div>
          </td>
        </tr>
      );
    });
    return users;
  };

  const handleClick = (data, e) => {
    // let url = "http://localhost:8000/tracenet_user/" + data.id;
    if (e.target.value == "Login") {
      console.log(data);
      navigate("/dashboard/tracenet", { state: data });
    } else if (e.target.value == "Update") {
      navigate("/dashboard/");
      //   axios
      //     .post(url, {
      //       headers: {
      //         "Content-Type": "application/json",
      //         Authorization: `Token ${user_data.key}`,
      //       },
      //     })
      //     .then((res) => {
      console.log(data);
      // });
    } else {
      {
        window.confirm("Are you sure you want to delete ?")
          ? axios
              .delete({
                headers: {
                  "Content-Type": "application/json",
                  Authorization: `Token ${user_data.key}`,
                },
              })
              .then((res) => {
                let filterData = user.filter((item, key) => {
                  console.log(item.id, data.id);
                  return item.id != data.id;
                });
                setUser(filterData);
              })
              .catch((err) => console.log(err))
          : console.log("false");
      }
    }
  };

  return (
    <div className="content-wrapper">
      <Loader />
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Tracenet Users</h3>
          <div className="card-tools">
            <ul className="pagination pagination-sm float-right">
              <li className="page-item">
                <a className="page-link" href="#">
                  «
                </a>
              </li>
              <li className="page-item">
                <a className="page-link" href="#">
                  1
                </a>
              </li>
              <li className="page-item">
                <a className="page-link" href="#">
                  2
                </a>
              </li>
              <li className="page-item">
                <a className="page-link" href="#">
                  3
                </a>
              </li>
              <li className="page-item">
                <a className="page-link" href="#">
                  »
                </a>
              </li>
            </ul>
          </div>
        </div>
        <div className="card-body p-0">
          <table className="table">
            <thead>
              <tr>
                <th style={{ width: 10 }}>S.no</th>
                <th>Username</th>
                <th className="table_head">Password</th>
                <th>Active Status</th>
                <th>Created at</th>
                <th>Updated At</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {handleClickEvent()}
              {/* {user.map((item, key) => (
                <tr>
                  <td>{key + 1}</td>
                  <td>{item.tracenet_username}</td>
                  <td className="table_data">{item.tracenet_password}</td>
                  <td>{String(item.is_active)}</td>
                  <td>
                    {new Date(item.created_at).toISOString().split("T")[0]}
                  </td>
                  <td>
                    {new Date(item.updated_at).toISOString().split("T")[0]}
                  </td> 
                  <td>
                  <div className="btn-group">
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Login"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Login
                    </button>
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Delete"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Delete
                    </button>
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Update"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Update
                    </button>
                  </div>
                </td>
               </tr>
              ))} */}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default User_detail;
