import React, { useState } from 'react';
import { Dropdown, Button } from 'react-bootstrap'
import './download.css'
import { Form } from 'react-bootstrap';

function Download() {
    const [options, setOptions] = useState({});


    const handleChange = (event) => {
        event.persist();
        const name = event.target.name;
        const value = event.target.value;
        console.log(value, options)

        setOptions(values => ({ ...values, [name]: value }))

        // setOption(value);
      };
      const handleSubmit = (event) => {
        const form = event.currentTarget;
        event.preventDefault();
        if (form.checkValidity() === false) {
              event.preventDefault();
              event.stopPropagation();
            }
        // setValidated(true);
        // console.log(inputs);
        console.log(options);
        // console.log(item)
      }

    return (
        <div class="content-wrapper">
            <div class="content">
                <div class="container-fluid">
                    <div class="card col-md-6 ml-auto mr-auto" >
                        <div class="card-header">Select Flow</div>
                        <div class="card-body">
                            <h6 class="card-title text-dark">Flow Name</h6>
                            <p class="card-text">
                               <Form onSubmit={handleSubmit}>
                            <Form.Select aria-label="Default select example" size='lg' className='selectflow' name='flow' value={options.flow || ""} onChange={handleChange}>
                      <option>select Flow</option>
                      <option value="Farmer Registration">Farmer Registration</option>
                      <option value="Internal Inspection">Internal Inspection</option>
                      <option value="Lot Creation">Lot Creation</option>
                      <option value="Crop Entry">Crop Entry</option>
                    </Form.Select>
                    <Button className='downloadbtn' variant='dark' type>Download</Button>
                    </Form>
                                {/* </Form> */}
                                {/* <Dropdown >
                                    <Dropdown.Toggle className='drop'>
                                        select flow to download formate
                                    </Dropdown.Toggle>
                                    <Dropdown.Menu className='dropmenu'>
                                        <Dropdown.Item>
                                            Farmer Registration
                                        </Dropdown.Item>
                                        <Dropdown.Item>
                                            Internal Inspection
                                        </Dropdown.Item>
                                        <Dropdown.Item>
                                            Lot Creation
                                        </Dropdown.Item>
                                        <Dropdown.Item>
                                            Crop Entry
                                        </Dropdown.Item>
                                    </Dropdown.Menu>
                                </Dropdown> */}

                            </p>

                            {/* <button type="button" class="btn btn-dark btn-block"><i class="fa fa-download"></i>Download</button> */}
                        </div>
                    </div>

                </div>
            </div>
        </div>
    )
}

export default Download
