import axios from "axios";
import React, { useState, useEffect } from "react";
import { Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./user_detail.css";

function User_detail() {
  const [user, setUser] = useState([]);
  const navigate = useNavigate();
  const user_data = JSON.parse(localStorage.getItem("user_data"));

  useEffect(() => {
    axios
      .get("http://localhost:8000/tracenet_user/", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Token ${user_data.key}`,
        },
      })
      .then((customers) => {
        console.log(customers.data);
        setUser(customers.data);
      });
  }, []);

  const handleClick = (data, e) => {
    let url = "http://localhost:8000/tracenet_user/" + data.id;
    if (e.target.value == "Login") {
      console.log(data);
      console.log(navigate("/dashboard/tracenet", { state: data }));
    } else if (e.target.value == "Update") {
      axios
        .post(url, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Token ${user_data.key}`,
          },
        })
        .then((res) => {

        });
    } else {
      {
        window.confirm("Are you sure you want to delete ?")
          ? axios
              .delete(url, {
                headers: {
                  "Content-Type": "application/json",
                  Authorization: `Token ${user_data.key}`,
                },
              })
              .then((res) => {
                let filterData = user.filter((item, key) => {
                  return item.id != data.id;
                });
                setUser(filterData);
              })
              .catch((err) => console.log(err))
          : console.log("false");
      }
    }
  };

  return (
    <div>
      <div className="content-wrapper">
        <Table striped bordered hover ons>
          <thead>
            <tr>
              <th>User_id</th>
              <th>Username</th>
              <th className="table_head">Password</th>
              <th>Active Status</th>
              <th>Created at</th>
              <th>Updated At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {user.map((item, key) => (
              <tr>
                <td>{item.id}</td>
                <td>{item.tracenet_username}</td>
                <td className="table_data">{item.tracenet_password}</td>
                <td>{String(item.is_active)}</td>
                <td>{item.created_at}</td>
                <td>{item.updated_at}</td>
                <td>
                  <div className="btn-group">
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Login"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Login
                    </button>
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Delete"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Delete
                    </button>
                    <button
                      type="button"
                      className="btn btn-default"
                      value={"Update"}
                      onClick={(e) => handleClick(item, e)}
                    >
                      Update
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default User_detail;
