import React, { useState, useEffect } from 'react';
import { Button, Form, InputGroup, Row, Col } from 'react-bootstrap'
import './tracenetForm.css'
// import axios from 'axios';

function TracenetForm() {

  const [textInputs, setInputs] = useState({});
  const [validated, setValidated] = useState(false);
  const [flowOption, setFlowoption] = useState({});
  const [isCheckein, setIschecked] = useState(false);
  // const [item, setItem] = useState({ kindOfStand: "" });
  // const { kindOfStand } = item;
  const [radioOption, setRadioOption] = useState({});
  // const { kindOfStand } = radioOption;

  const obj = [
    {
      "option": "Internal Inspection",
      "nested_option": [{
        "Select_Scope": {
          "mid_year": "Mid Year",
          "scope_renewal": "Scope Renewal"
        },
        "Select_Year": {
          "Current": "Current",
          "Previous": "Previous"
        }

      }]
    },
    {
      "option": "Harvesting",
      "nested_option": [{
        "Select_Scope": {
          "current": "Current",
          "previous": "Previous"
        },
        "Select_Season": {
          "kharif": "Kharif",
          "rabi": "Rabi",
          "zaid": "Zaid",
          "perennial": "Perennial"
        },
        "Farm_Type": {
          "single_farm": "Single Farm",
          "multi_farm": "Multi_Farm"
        }
      }]
    },
    {
      "option": "Lot Creation",
      "nested_option": [{
        "Select_Scope": {
          "current": "Current",
          "previous": "Previous"
        },
        "Organic_Status": {
          "organic": "Organic",
          "2nd year conversion": "2nd Year Conversion",
          "1st year conversion": "1st Year Conversion",
          "3rd year conversion": "3rd Year Conversion",
          "2nd year conversion to organic": "2nd Year Conversion to Organic",
          "3rd year conversion to organic": "3rd Year Conversion to Organic"
        }
      }]

    },
    {
      "option": "Crop Entry",
      "nested_option": [{
        "select_Season": {
          "kharif": "Kharif",
          "rabi": "Rabi",
          "zaid": "Zaid",
          "perennial": "Perennial",
          "post harvest activities": "Post Harvest Activities"
        },
        "Select_Scope": {
          "old_ICS": "Old ICS",
          "new_ICS": "New ICS",
          "amendment": "Amendment"
        }
      }]
    }
  ]

  const handleChange = (event) => {

    const name = event.target.name;
    const value = event.target.value;

    setInputs(values => ({ ...values, [name]: value }))
    setFlowoption(values => ({ ...values, [name]: value }))
    // setItem(prevState => ({ ...prevState, [name]: value }));
    console.log(event.target.value);
    radioView(event.target.value)
  };


  const handleSubmit = (event) => {
    const form = event.currentTarget;
    // event.preventDefault();1
    console.log("-----inouts----", textInputs)

    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
    setValidated(true);
  }

  const radioSelector=(e)=>{
    alert('dfd')
    // e.preventDefault();]
    console.log(e.target.id ,e.target.key,e.target.value);

    const name =  e.target.id
    const key = e.target.name;
    const value = e.target.value;
    let item = {
      [name]: value
    }
    setRadioOption(values => ({ ...values, [name]: item })) 
    console.log(radioOption);
    console.log(textInputs)
  }

  const radioView = (list) => {
    console.log(flowOption.flow)
    let template =
      obj.map(data => {
        if (data.option === flowOption.flow) {
          return (data.nested_option.map((n_val) => {
            return (
              <div key={data.option}>{Object.entries(n_val).map(d => {
                return (
                  <>
                    <Form.Label key={d[0]}>{d[0]}</Form.Label><br />
                    {
                      Object.values(d[1]).map(n => {
                        return (
                          <>
                            <Form.Check
                              inline
                              name={n}
                              id={d[0]}
                              aria-label='radio 1'
                              type="radio"
                              label={n}
                              checked={isCheckein ? true : isCheckein[n]}
                              onChange={radioSelector}
                            /><br />
                          </>
                        )
                      })}
                  </>
                )
              })}
              </ div>
            )
          })
          )
        }
      })
    return template
  }

  return (
    <div className="content-wrapper">
      <div className="content">
        <div className="container-fluid">
          <div className="card" >
            <div className="card-header">Sign In using Tracenet credentials</div>
            <div className="card-body">
              <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <Row className="mb-3">
                  <Form.Group as={Col} md="8" controlId="validationCustomUsername">
                    <Form.Label>Username</Form.Label>
                    <InputGroup hasValidation>
                      <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                      <Form.Control
                        type="text"
                        placeholder="Username"
                        aria-describedby="inputGroupPrepend"
                        required
                        name="username"
                        onChange={handleChange}
                        value={textInputs.username || ""}
                      />
                      <Form.Control.Feedback type="invalid">
                        Please choose a username.
                      </Form.Control.Feedback>
                    </InputGroup>
                  </Form.Group>
                  <Form.Group as={Col} md="8" controlId="validationCustom01">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      name="password"
                      placeholder="****"
                      value={textInputs.password || ""}
                      onChange={handleChange}
                    />
                    <Form.Control.Feedback type="invalid">Incorrect Password</Form.Control.Feedback>
                  </Form.Group>
                  <Form.Group as={Col} md="8" controlId="validationCustom02">
                    <Form.Label>Flow</Form.Label><br></br>
                    <Form.Select aria-label="Default select example" size='lg' className='selectflow' name='flow' value={flowOption.flow || ""} onChange={handleChange}>
                      <option>select Flow</option>
                      <option value="Farmer Registration">Farmer Registration</option>
                      <option value="Internal Inspection">Internal Inspection</option>
                      <option value="Lot Creation">Lot Creation</option>
                      <option value="Crop Entry">Crop Entry</option>
                      <option value="Harvesting">Harvesting</option>
                    </Form.Select>
                    <Form.Control.Feedback type="invalid">Looks good!</Form.Control.Feedback>
                  </Form.Group>

                </Row>
                <Form.Group className="mb-3">

                  <Form.Group as={Col} md="12" controlId="validationCustom02" >
                    {
                      radioView()
                    }
                  </Form.Group>

                  <Form.Group as={Col} md="8" controlId="validationCustom03" >
                    <Form.Label>Select File</Form.Label><br></br>
                    <Form.Control
                      required
                      type="file"
                      name="file"
                      value={textInputs.file || ""}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Check
                    required
                    label="I agree that this tool will fill the captcha and data on Tracenet website on my behalf."
                    feedback="You must agree before submitting."
                    feedbackType="invalid"
                  />
                </Form.Group>
                <Button type="submit">Submit form</Button>
              </Form>
            </div>
          </div>

        </div>
      </div>
    </div>
  )
}

export default TracenetForm
